Ext.define('CMS.controller.Login', {
    extend: 'Ext.app.Controller',

    views: [
        'LoginForm'
    ],

    refs: [
        {
            ref: 'loginForm',
            selector: 'loginForm'
        }
    ],

    onLoginClick: function(button, e, options) {
        this.login();
    },

    onSpecialkey: function(field, e) {
        if(e.getKey() == e.ENTER) { 
            this.login();
        }
    },

    login: function() {
        var loginForm = this.getLoginForm();
        var form = loginForm.getForm();
        var main = this.getController('CMS.controller.Main');
        if (form.isValid()) {
            form.submit({
                success: function(form, action) {
                    Ext.Msg.alert('成功', '登录成功!');
                    loginForm.hide();
                    main.showMainView();
                },
                failure: function(form, action) {
                    Ext.Msg.alert('失败', action.result.msg);
                }
            });
        }
    },

    init: function(application) {
        Ext.create('CMS.view.LoginForm', {});

        this.control({
            "button[id=loginBtn]": {
                click: this.onLoginClick
            },
            "textfield": {
                specialkey: this.onSpecialkey
            }
        });
    },

    showLogin: function() {
        this.getLoginForm().show();
    }

});
